import csv, json
path='data/causal_demo.csv'
data={}
with open(path, newline='') as f:
    r=csv.DictReader(f)
    for row in r:
        for k,v in row.items():
            try:
                # try float coercion
                val = float(v)
            except Exception:
                val = v
            data.setdefault(k, []).append(val)
print(json.dumps({'data': data}))
