import json

search_results_data = {{acquire_domain_knowledge.results}}

# The FallbackSearchTool returns a list of dictionaries
if isinstance(search_results_data, list) and len(search_results_data) > 0:
    print(json.dumps({'status': 'Success', 'message': f'{len(search_results_data)} search results are valid.'}))
else:
    print(json.dumps({'status': 'Failure', 'message': 'Search returned no results. Halting workflow.'}))

